package vaishakproject;


public class Pattern5 {

	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="vaishak";
		int n=7;
				for(int i=n;i>=1;i--)
				{
					for(int j=0;j<i;j++)
					
						{
						System.out.print(s.charAt(j)+" ");
						}
					System.out.println("");

				}
				

	}

}
